import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getSellingProducts } from "../../redux/features/productsSlice";
import "../../styles/Sellings/Sellings.css";

function Sellings() {
  const dispatch = useDispatch();
  const { sellingProducts } = useSelector((state) => state.products);
  console.log(sellingProducts);
  const handleClassNameActive = (e) => {
    const listItems = Array.from(e.target.parentElement.children);
    listItems.forEach((item) => {
      item.classList.remove("active");
      e.target.classList.add("active");
    });
  };
  useEffect(() => {
    dispatch(getSellingProducts());
  }, [dispatch]);
  return (
    <div className="sellings">
      <div className="container p-0">
        <div className="products-settings">
          <h3>Top Selling</h3>
          <ul>
            <li className="active">All</li>
            <li>Smartphones</li>
            <li>Watches</li>
            <li>Headphones</li>
            <li>Laptops</li>
            <li>Cameras</li>
          </ul>
        </div>
        <div className="sellings-content">
          {sellingProducts.map((item) => (
            <div key={item._id} className="product-item">
              <img src={item.image} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Sellings;
