import React from "react";
import { useSelector, useDispatch } from "react-redux";
import "../../styles/Productdetails/Productdetails.css";

function Productdetails() {
  const { product } = useSelector((state) => state.products);
  product.hasOwnProperty("_id")
    ? console.log(product)
    : console.log("undefined");
  return (
    <div className="details">
      <div className="container">
        <div className="row">
          <div className="details-image col-lg-4">
            <img src={product.image} />
          </div>
          <div className="details-info col-lg-8 text-left">
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <span className="newprice">${product.newprice}</span>
            {product.oldprice && (
              <span className="oldprice">${product.oldprice}</span>
            )}
            <div className="add-div-details">
              <button className="btn-details">Add To Cart</button>
              <button className="btn-details">Add To Favorite</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Productdetails;
