import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  filterSellingsProductsByCategory,
  getSellingProducts,
} from "../../redux/features/productsSlice";
import "../../styles/Sellings/Sellings.css";

function Sellings() {
  const dispatch = useDispatch();
  const { sellingProducts } = useSelector((state) => state.products);
  const handleClassNameActive = (e) => {
    const listItems = Array.from(e.target.parentElement.children);
    listItems.forEach((item) => {
      item.classList.remove("active");
      e.target.classList.add("active");
    });
  };
  const handleFilterSellingProducts = (e, category) => {
    handleClassNameActive(e);
    dispatch(filterSellingsProductsByCategory(category));
  };
  let i = 1;
  let j = 0;
  const getNext = () => {
    const allItems = Array.from(document.querySelectorAll(".selling-item"));
    allItems.forEach((item) => {
      let style = window.getComputedStyle(item, null);
      let marginRight = style.getPropertyValue("margin-right");
      let value = marginRight.slice(0, marginRight.indexOf("px"));
      item.style.transform = `translateX(calc(-${100 * i}% - ${i * value}px))`;
    });
    if (i < 9) {
      i++;
    } else {
      i = 1;
    }
  };
  const getPrev = () => {
    const allItems = Array.from(document.querySelectorAll(".selling-item"));
    allItems.forEach((item) => {
      let style = window.getComputedStyle(item, null);
      let marginRight = style.getPropertyValue("margin-right");
      let value = marginRight.slice(0, marginRight.indexOf("px"));
      item.style.transform = `translateX(calc(${100 * j}% + ${j * value}px))`;
    });
    j++;
  };
  useEffect(() => {
    dispatch(getSellingProducts());
  }, [dispatch]);
  return (
    <div className="sellings">
      <div className="container">
        <div className="products-settings">
          <h3>Top Selling</h3>
          <ul>
            <li
              className="active"
              onClick={(e) => handleFilterSellingProducts(e, "all")}
            >
              All
            </li>
            <li onClick={(e) => handleFilterSellingProducts(e, "mobiles")}>
              Smartphones
            </li>
            <li onClick={(e) => handleFilterSellingProducts(e, "watches")}>
              Watches
            </li>
            <li onClick={(e) => handleFilterSellingProducts(e, "headphones")}>
              Headphones
            </li>
            <li onClick={(e) => handleFilterSellingProducts(e, "laptops")}>
              Laptops
            </li>
            <li onClick={(e) => handleFilterSellingProducts(e, "cameras")}>
              Cameras
            </li>
          </ul>
        </div>
        <div className="sellings-content mb-4">
          {sellingProducts.map((item) => (
            <div className="selling-item" key={item._id}>
              <img src={item.image} />
              <div className="product-item-details">
                <h5>{item.name}</h5>
                <div className="prices">
                  <h6 className="newprice">${item.newprice}</h6>
                  {item.oldprice && (
                    <p className="oldprice">${item.oldprice}</p>
                  )}
                </div>
              </div>
              <div className="product-item-icones">
                <i className="fa fa-heart-o"></i>
                <i className="fa fa-shopping-cart"></i>
                <i className="fa fa-eye"></i>
              </div>
              <div className="add">
                <button>Add To Cart</button>
              </div>
            </div>
          ))}
        </div>
        <div className="btns">
          <i className="fa fa-arrow-left" onClick={getPrev}></i>
          <i className="fa fa-arrow-right" onClick={getNext}></i>
        </div>
      </div>
    </div>
  );
}

export default Sellings;
