import { configureStore } from "@reduxjs/toolkit";
import authSlice from "./features/authSlice";
import productsSlice from "./features/productsSlice";
const store = configureStore({
  reducer: {
    products: productsSlice,
    auth: authSlice,
  },
});
export default store;
